import { Component } from '@angular/core';

@Component({
  selector: 'app-componente03',
  standalone: true,
  templateUrl: './componente03.component.html',
  styleUrls: ['./componente03.component.css'], // Fix the typo from 'styleUrl' to 'styleUrls'
})
export class Componente03Component {
  // Variable for the image
  imagem: string =
    'data:image/jpeg;base64,';

  // Function to toggle the image
  AlterarImg() {
    if (this.imagem === 'data:image/jpeg;base64,') {
      this.imagem = 'data:image/jpeg;base64,';
    } else {
      this.imagem = 'data:image/jpeg;base64,';
    }
  }
}
